(392, 'sbr_notifications', 'a:4:{s:6:\"update\";i:1700936003;s:4:\"feed\";a:1:{i:0;a:8:{s:5:\"title\";s:57:\"Black Friday is here. Save up to 70% off on {plugin} Pro!\";s:7:\"content\";s:268:\"Our MOST MASSIVE sale of the year is happening right now! Score up to 70% off your Smash Balloon plan to embed social feeds from TikTok, Facebook, Instagram, YouTube, Twitter, Google Reviews and more. Act fast – claim your huge discount while it’s still available.\";s:5:\"image\";s:7:\"balloon\";s:4:\"type\";a:1:{i:0;s:4:\"free\";}s:2:\"id\";i:18;s:4:\"btns\";a:2:{s:7:\"primary\";a:3:{s:3:\"url\";s:153:\"https://smashballoon.com/black-friday/?utm_source={campaign}&utm_medium=plugin-dashboard-notices&utm_campaign=bf2023&utm_content=lite-claim-your-discount\";s:4:\"text\";s:24:\"Claim Your Huge Discount\";s:4:\"attr\";a:1:{i:0;s:11:\"targetblank\";}}s:9:\"secondary\";a:3:{s:3:\"url\";s:143:\"https://smashballoon.com/black-friday/?utm_source={campaign}&utm_medium=plugin-dashboard-notices&utm_campaign=bf2023&utm_content=lite-see-deals\";s:4:\"text\";s:22:\"See Black Friday Deals\";s:4:\"attr\";a:1:{i:0;s:11:\"targetblank\";}}}s:5:\"start\";s:19:\"2023-11-20 06:00:00\";s:3:\"end\";s:19:\"2023-11-30 23:59:59\";}}s:6:\"events\";a:0:{}s:9:\"dismissed\";a:0:{}}', 'yes'),
(393, 'sbr_newuser_notifications', 'a:4:{s:6:\"update\";i:1700598211;s:4:\"feed\";a:2:{s:6:\"review\";a:6:{s:5:\"title\";s:25:\"How Do You Like {plugin}?\";s:7:\"content\";s:260:\"It\'s great to see that you\'ve been using the <strong>{plugin}</strong> plugin for a while now. Hopefully you\'re happy with it!&nbsp; If so, would you consider leaving a positive review? It really helps to support the plugin and helps others to discover it too!\";s:2:\"id\";s:6:\"review\";s:5:\"image\";s:12:\"sbr-icon.png\";s:4:\"btns\";a:4:{s:7:\"primary\";a:4:{s:3:\"url\";s:12:\"{review-url}\";s:4:\"attr\";a:1:{i:0;s:11:\"targetblank\";}s:5:\"class\";s:18:\"sbr_notice_dismiss\";s:4:\"text\";s:18:\"Sure, I\'d love to!\";}s:7:\"dismiss\";a:3:{s:3:\"url\";a:1:{s:28:\"sbr_ignore_rating_notice_nag\";s:1:\"1\";}s:5:\"class\";s:18:\"sbr_notice_dismiss\";s:4:\"text\";s:9:\"No thanks\";}s:8:\"complete\";a:3:{s:3:\"url\";a:1:{s:28:\"sbr_ignore_rating_notice_nag\";s:1:\"1\";}s:5:\"class\";s:18:\"sbr_notice_dismiss\";s:4:\"text\";s:27:\"I\'ve already given a review\";}s:5:\"later\";a:3:{s:3:\"url\";a:1:{s:28:\"sbr_ignore_rating_notice_nag\";s:5:\"later\";}s:5:\"class\";s:18:\"sbr_notice_dismiss\";s:4:\"text\";s:12:\"Ask Me Later\";}}s:4:\"wait\";s:2:\"14\";}s:8:\"discount\";a:8:{s:5:\"title\";s:28:\"Attention {platform} Lovers!\";s:7:\"content\";s:195:\"<strong>Exclusive offer!</strong> We don\'t run promotions very often, but for a limited time we\'re offering <strong>{amount} off</strong> our Pro version to all users of our free {plugin} plugin.\";s:2:\"id\";s:8:\"discount\";s:6:\"amount\";s:3:\"60%\";s:5:\"image\";s:12:\"sbr-icon.png\";s:13:\"image_overlay\";s:7:\"60% off\";s:4:\"btns\";a:2:{s:7:\"primary\";a:4:{s:3:\"url\";s:127:\"https://smashballoon.com/{slug}/?utm_campaign={campaign}&utm_source=notices&utm_medium=newuser&discount={lowerplatform}thankyou\";s:5:\"class\";s:32:\"sbr_notice_dismiss sbr_offer_btn\";s:4:\"text\";s:14:\"Get this offer\";s:4:\"attr\";a:1:{i:0;s:11:\"targetblank\";}